import React, { useState } from "react";
import axios from "axios";

function App() {
  const [profile, setProfile] = useState("");
  const [transactions, setTransactions] = useState(null);
  const [payments, setPayments] = useState(null);
  const [result, setResult] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!transactions) return alert("Please upload transactions file.");

    const formData = new FormData();
    formData.append("profile_text", profile);
    formData.append("transactions_file", transactions);
    if (payments) formData.append("payments_file", payments);

    try {
      const res = await axios.post("http://127.0.0.1:8000/investor_opportunity", formData);
      setResult(res.data);
    } catch (err) {
      console.error(err);
      alert("Error: " + err.message);
    }
  };

  const downloadJSON = () => {
    const blob = new Blob([JSON.stringify(result, null, 2)], { type: "application/json" });
    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = "investment_analysis.json";
    a.click();
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>Investor Opportunity Engine</h1>
      <form onSubmit={handleSubmit}>
        <textarea
          placeholder="Enter company profile..."
          value={profile}
          onChange={(e) => setProfile(e.target.value)}
          style={{ width: "100%", height: "80px", marginBottom: "10px" }}
        />
        <br />
        <input type="file" onChange={(e) => setTransactions(e.target.files[0])} />
        <br />
        <input type="file" onChange={(e) => setPayments(e.target.files[0])} />
        <br /><br />
        <button type="submit">Analyze</button>
      </form>

      {result && (
        <div style={{ marginTop: "30px" }}>
          <h2>📊 Investment Analysis</h2>

          {/* Metrics Summary */}
          <div style={{ border: "1px solid #ccc", padding: "15px", marginBottom: "15px", borderRadius: "8px" }}>
            <h3>Key Metrics</h3>
            <p><strong>Total Revenue:</strong> ${result.metrics.total_revenue?.toLocaleString()}</p>
            <p><strong>Total Cost:</strong> ${result.metrics.total_cost?.toLocaleString()}</p>
            <p><strong>Net Profit:</strong> ${result.metrics.net_profit?.toLocaleString()}</p>
            <p><strong>ROI %:</strong> {result.metrics.roi_percent ? result.metrics.roi_percent.toFixed(2) + "%" : "N/A"}</p>
            <p><strong>Growth Rate %:</strong> {result.metrics.growth_rate_percent ? result.metrics.growth_rate_percent.toFixed(2) + "%" : "N/A"}</p>
          </div>

          {/* Investment Advice */}
          {result.llm_investment_advice && (
            <div style={{ border: "1px solid #ccc", padding: "15px", marginBottom: "15px", borderRadius: "8px" }}>
              <h3>AI Investment Advice</h3>
              <p><strong>Summary:</strong> {result.llm_investment_advice.investment_summary}</p>
              <p><strong>Recommended Investment:</strong> ${result.llm_investment_advice.recommended_investment_amount?.toLocaleString()}</p>
              <p><strong>Expected Payback:</strong> {result.llm_investment_advice.expected_payback_months}</p>

              <h4>Strategy</h4>
              <ul>
                {result.llm_investment_advice.high_level_strategy?.map((s, idx) => (
                  <li key={idx}>{s}</li>
                ))}
              </ul>

              <h4>Main Risks</h4>
              <ul>
                {result.llm_investment_advice.main_risks?.map((r, idx) => (
                  <li key={idx}>{r}</li>
                ))}
              </ul>

              <p><strong>Confidence:</strong> {result.llm_investment_advice.confidence}</p>
            </div>
          )}

          {/* ROI Projections */}
          {result.roi_projections && (
            <div style={{ border: "1px solid #ccc", padding: "15px", borderRadius: "8px" }}>
              <h3>ROI Projections</h3>
              <p><strong>Baseline Min Investment ROI:</strong> {result.roi_projections.baseline?.roi_percent_min_investment?.toFixed(2)}%</p>
              <p><strong>Baseline Max Investment ROI:</strong> {result.roi_projections.baseline?.roi_percent_max_investment?.toFixed(2)}%</p>
              <p><strong>Projected Min Investment ROI (with uplift):</strong> {result.roi_projections.with_uplift?.projected_roi_percent_min_investment?.toFixed(2)}%</p>
              <p><strong>Projected Max Investment ROI (with uplift):</strong> {result.roi_projections.with_uplift?.projected_roi_percent_max_investment?.toFixed(2)}%</p>
            </div>
          )}

          {/* Download JSON Button */}
          <br />
          <button onClick={downloadJSON}>Download JSON</button>
        </div>
      )}
    </div>
  );
}

export default App;
