import React, { useState } from "react";
import {
  Container,
  Typography,
  Paper,
  Grid,
  Button,
  CircularProgress,
  Box
} from "@mui/material";
import { styled } from "@mui/system";
import axios from "axios";
import {
  ResponsiveContainer,
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  PieChart,
  Pie,
  Cell
} from "recharts";

const CardBox = styled(Paper)(({ theme, bgcolor }) => ({
  padding: theme.spacing(2),
  borderRadius: 16,
  color: "#fff",
  backgroundColor: bgcolor || theme.palette.primary.main,
  boxShadow: "0 4px 12px rgba(0,0,0,0.1)",
  height: 120,
  display: "flex",
  flexDirection: "column",
  justifyContent: "center",
  textAlign: "center",
}));

const COLORS = ["#4caf50", "#f44336"]; // Green and red for pie chart

export default function App() {
  const [transactions, setTransactions] = useState(null);
  const [payments, setPayments] = useState(null);
  const [profileText, setProfileText] = useState("");
  const [loading, setLoading] = useState(false);
  const [result, setResult] = useState(null);

  const handleSubmit = async () => {
    if (!transactions || !profileText.trim()) {
      alert("Please upload transactions and enter profile text.");
      return;
    }
    setLoading(true);
    const formData = new FormData();
    formData.append("profile_text", profileText);
    formData.append("transactions_file", transactions);
    if (payments) formData.append("payments_file", payments);

    try {
      const res = await axios.post(
        "http://127.0.0.1:8000/investor_opportunity",
        formData,
        { headers: { "Content-Type": "multipart/form-data" } }
      );
      setResult(res.data);
    } catch (err) {
      console.error(err);
      alert("Error fetching data.");
    }
    setLoading(false);
  };

  // Prepare pie chart data for profit vs cost
  const pieData = result
    ? [
        { name: "Profit", value: result.metrics?.profit || 0 },
        { name: "Cost", value: result.metrics?.cost || 0 }
      ]
    : [];

  // Prepare bar chart data for ROI projections
  const roiData = result?.roi_projections?.with_uplift || {};
  const barData = Object.entries(roiData).map(([key, value]) => ({
    name: key.replace(/_/g, " "),
    value: typeof value === "number" ? value : 0,
  }));

  // AI Advice text extraction
  const advice = result?.ai_investment_advice || {};

  // Color-coded metric cards config
  const metricCards = [
    {
      label: "Total Revenue",
      value: result?.metrics?.total_revenue
        ? `$${Number(result.metrics.total_revenue).toLocaleString()}`
        : "N/A",
      bgcolor: "#4caf50"
    },
    {
      label: "ROI",
      value: result?.metrics?.roi ? `${result.metrics.roi}%` : "N/A%",
      bgcolor: "#2196f3"
    },
    {
      label: "Growth",
      value: result?.metrics?.growth ? `${result.metrics.growth}%` : "N/A%",
      bgcolor: "#ff9800"
    },
    {
      label: "Valuation",
      value: result?.metrics?.valuation
        ? `$${Number(result.metrics.valuation).toLocaleString()}`
        : "N/A",
      bgcolor: "#7b1fa2"
    },
    {
      label: "Risk",
      value: result?.risk?.level || "N/A",
      bgcolor: "#f44336"
    }
  ];

  const handleDownloadJSON = () => {
    const jsonStr = JSON.stringify(result, null, 2);
    const blob = new Blob([jsonStr], { type: "application/json" });
    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = "full_result.json";
    a.click();
    URL.revokeObjectURL(url);
  };

  return (
    <Container maxWidth="lg" sx={{ paddingY: 4 }}>
      <Typography variant="h4" fontWeight="bold" gutterBottom>
        Investor Opportunity Engine
      </Typography>

      {/* Upload and Profile Input */}
      <Grid container spacing={3} sx={{ mb: 4 }}>
        <Grid item xs={12} md={4}>
          <Paper
            elevation={3}
            sx={{ padding: 3, borderRadius: 3, height: "100%" }}
          >
            <Typography variant="h6" gutterBottom>
              Upload Transactions
            </Typography>
            <input
              type="file"
              onChange={(e) => setTransactions(e.target.files[0])}
              style={{ marginBottom: 15 }}
            />
            <Typography variant="h6" gutterBottom>
              Upload Payments (Optional)
            </Typography>
            <input
              type="file"
              onChange={(e) => setPayments(e.target.files[0])}
              style={{ marginBottom: 15 }}
            />
            <Typography variant="h6" gutterBottom>
              Company Profile
            </Typography>
            <textarea
              placeholder="Enter company profile..."
              value={profileText}
              onChange={(e) => setProfileText(e.target.value)}
              style={{
                width: "100%",
                height: 120,
                borderRadius: 12,
                padding: 12,
                border: "1px solid #ccc",
                resize: "none"
              }}
            />
            <Button
              variant="contained"
              fullWidth
              sx={{ mt: 2, borderRadius: 2 }}
              onClick={handleSubmit}
              disabled={loading || !transactions || !profileText.trim()}
            >
              {loading ? <CircularProgress size={24} color="inherit" /> : "Analyze"}
            </Button>
          </Paper>
        </Grid>

        {/* Results Section */}
        <Grid item xs={12} md={8}>
          {!result && !loading && (
            <Typography variant="body1" color="textSecondary">
              Upload data and click "Analyze" to see results.
            </Typography>
          )}

          {loading && <CircularProgress />}

          {result && (
            <>
              {/* Metric Cards */}
              <Grid container spacing={2} sx={{ mb: 3 }}>
                {metricCards.map(({ label, value, bgcolor }) => (
                  <Grid item xs={12} sm={6} md={2.4} key={label}>
                    <CardBox bgcolor={bgcolor}>
                      <Typography variant="subtitle1" fontWeight="bold">
                        {label}
                      </Typography>
                      <Typography variant="h6" sx={{ mt: 1 }}>
                        {value}
                      </Typography>
                    </CardBox>
                  </Grid>
                ))}
              </Grid>

              {/* Charts */}
              <Grid container spacing={3} sx={{ mb: 3 }}>
                <Grid item xs={12} md={5}>
                  <Typography variant="subtitle1" gutterBottom>
                    Profit vs Cost
                  </Typography>
                  <ResponsiveContainer width="100%" height={250}>
                    <PieChart>
                      <Pie
                        data={pieData}
                        dataKey="value"
                        nameKey="name"
                        outerRadius={80}
                        fill="#8884d8"
                        label
                      >
                        {pieData.map((entry, index) => (
                          <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                        ))}
                      </Pie>
                      <Tooltip />
                    </PieChart>
                  </ResponsiveContainer>
                </Grid>
                <Grid item xs={12} md={7}>
                  <Typography variant="subtitle1" gutterBottom>
                    ROI Projections
                  </Typography>
                  <ResponsiveContainer width="100%" height={250}>
                    <LineChart data={barData} margin={{ top: 5, right: 20, bottom: 5, left: 0 }}>
                      <CartesianGrid strokeDasharray="3 3" />
                      <XAxis dataKey="name" />
                      <YAxis />
                      <Tooltip />
                      <Line
                        type="monotone"
                        dataKey="value"
                        stroke="#4caf50"
                        strokeWidth={3}
                        dot={{ r: 4 }}
                      />
                    </LineChart>
                  </ResponsiveContainer>
                </Grid>
              </Grid>

              {/* AI Investment Advice */}
              <Paper
                elevation={3}
                sx={{ p: 3, borderRadius: 3, backgroundColor: "#fff" }}
              >
                <Typography variant="h6" gutterBottom>
                  AI Investment Advice
                </Typography>

                <Typography>
                  <strong>Summary:</strong> {advice.summary || "N/A"}
                </Typography>
                <Typography>
                  <strong>Recommended Investment:</strong>{" "}
                  {advice.recommended_investment
                    ? `$${Number(advice.recommended_investment).toLocaleString()}`
                    : "N/A"}
                </Typography>
                <Typography>
                  <strong>Expected Payback:</strong> {advice.expected_payback || "N/A"}
                </Typography>

                {advice.strategy && advice.strategy.length > 0 && (
                  <>
                    <Typography sx={{ mt: 2, fontWeight: "bold" }}>
                      Strategy
                    </Typography>
                    <ul>
                      {advice.strategy.map((item, i) => (
                        <li key={i}>{item}</li>
                      ))}
                    </ul>
                  </>
                )}

                {advice.main_risks && advice.main_risks.length > 0 && (
                  <>
                    <Typography sx={{ mt: 2, fontWeight: "bold" }}>
                      Main Risks
                    </Typography>
                    <ul>
                      {advice.main_risks.map((risk, i) => (
                        <li key={i}>{risk}</li>
                      ))}
                    </ul>
                  </>
                )}

                <Typography sx={{ mt: 2 }}>
                  <strong>Confidence:</strong> {advice.confidence || "N/A"}
                </Typography>
              </Paper>

              {/* Download JSON Button */}
              <Box sx={{ mt: 3 }}>
                <Button variant="outlined" onClick={handleDownloadJSON}>
                  Download Full JSON
                </Button>
              </Box>
            </>
          )}
        </Grid>
      </Grid>
    </Container>
  );
}
