import React, { useState } from "react";
import axios from "axios";

function App() {
  const [transactionsFile, setTransactionsFile] = useState(null);
  const [paymentsFile, setPaymentsFile] = useState(null);
  const [profileText, setProfileText] = useState("");
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!transactionsFile || !paymentsFile) {
      alert("Please upload both files");
      return;
    }

    const formData = new FormData();
    formData.append("transactions", transactionsFile);
    formData.append("payments", paymentsFile);
    formData.append("profile_text", profileText);

    try {
      setLoading(true);
      const res = await axios.post("http://127.0.0.1:8000/docs", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      setResult(res.data);
    } catch (err) {
      console.error(err);
      alert("Error processing files");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ padding: "20px", fontFamily: "Arial" }}>
      <h1>Opportunity Engine</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Transactions CSV/XLSX:</label>
          <input type="file" accept=".csv,.xlsx" onChange={(e) => setTransactionsFile(e.target.files[0])} />
        </div>
        <div>
          <label>Payments CSV/XLSX:</label>
          <input type="file" accept=".csv,.xlsx" onChange={(e) => setPaymentsFile(e.target.files[0])} />
        </div>
        <div>
          <label>Company Profile:</label>
          <textarea
            rows="4"
            style={{ width: "100%" }}
            value={profileText}
            onChange={(e) => setProfileText(e.target.value)}
            placeholder="Describe the company..."
          />
        </div>
        <button type="submit" disabled={loading}>
          {loading ? "Processing..." : "Analyze"}
        </button>
      </form>

      {result && (
        <div style={{ marginTop: "20px" }}>
          <h2>Analysis Result</h2>
          <pre style={{ background: "#f4f4f4", padding: "10px" }}>
            {JSON.stringify(result, null, 2)}
          </pre>
        </div>
      )}
    </div>
  );
}

export default App;
