import React, { useState } from 'react';
import { TrendingUp, Clock, Upload, FileText, DollarSign, BarChart3, Target, Shield, AlertTriangle, Download, Calculator, ArrowLeft } from 'lucide-react';

// Your existing OpportunitiesList component
const OpportunitiesList = ({ onAnalyze }) => {
  const opportunities = [
    {
      id: 1,
      title: "Instant health loans",
      priority: "High Priority",
      priorityColor: "#f97316",
      projectedROI: "340%",
      timeline: "6 months",
      partners: ["Ayala Healthcare", "Globe Telecom"],
      confidence: "93%",
      profileText: "Healthcare fintech startup providing instant medical loans to underserved populations. Uses AI for credit scoring and partners with major healthcare providers. Strong growth trajectory with 40% month-over-month user acquisition.",
      transactionsFile: "health-loans-transactions.csv",
      paymentsFile: "health-loans-payments.csv"
    },
    {
      id: 2,
      title: "Climate-based Insurance",
      priority: "Medium Priority",
      priorityColor: "#eab308",
      projectedROI: "185%",
      timeline: "8 months",
      partners: ["Weather Analytics", "AgriTech Corp"],
      confidence: "78%",
      profileText: "Insurtech company offering climate-risk insurance for farmers and small businesses. Uses satellite data and weather analytics for precise risk assessment. Growing demand due to climate change impacts.",
      transactionsFile: "climate-insurance-transactions.csv",
      paymentsFile: "climate-insurance-payments.csv"
    },
    {
      id: 3,
      title: "Rural MSMEs Cash Flow",
      priority: "High Priority",
      priorityColor: "#f97316",
      projectedROI: "185%",
      timeline: "6 months",
      partners: ["AgriTech Corp", "Rural Bank Network"],
      confidence: "78%",
      profileText: "Fintech platform providing working capital loans to rural micro, small and medium enterprises. Focus on agricultural supply chain financing with digital-first approach and alternative credit scoring.",
      transactionsFile: "rural-msmes-transactions.csv",
      paymentsFile: "rural-msmes-payments.csv"
    }
  ];

  const handleAnalyze = (opportunity) => {
    if (onAnalyze) {
      onAnalyze({
        profileText: opportunity.profileText,
        transactionsFile: opportunity.transactionsFile,
        paymentsFile: opportunity.paymentsFile,
        opportunityTitle: opportunity.title
      });
    }
  };

  const styles = {
    container: {
      minHeight: '100vh',
      backgroundColor: '#f5f5f7',
      fontFamily: 'system-ui, -apple-system, sans-serif'
    },
    header: {
      padding: '40px 20px',
      textAlign: 'center',
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      color: 'white'
    },
    logo: {
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      marginBottom: '16px',
      fontSize: '28px',
      fontWeight: 'bold'
    },
    logoIcon: {
      width: '36px',
      height: '36px',
      marginRight: '10px',
      background: 'linear-gradient(135deg, #ff6b35, #f7931e)',
      borderRadius: '10px',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center'
    },
    title: {
      fontSize: '24px',
      fontWeight: '700',
      margin: '0 0 6px 0'
    },
    subtitle: {
      fontSize: '14px',
      opacity: 0.9,
      margin: 0
    },
    content: {
      maxWidth: '400px',
      margin: '0 auto',
      padding: '16px'
    },
    card: {
      backgroundColor: 'white',
      borderRadius: '16px',
      padding: '20px',
      marginBottom: '12px',
      boxShadow: '0 2px 12px rgba(0,0,0,0.08)',
      border: '1px solid rgba(0,0,0,0.04)'
    },
    cardHeader: {
      display: 'flex',
      alignItems: 'flex-start',
      justifyContent: 'space-between',
      marginBottom: '16px'
    },
    cardTitle: {
      fontSize: '18px',
      fontWeight: '600',
      color: '#1d1d1f',
      margin: '0 0 6px 0',
      flex: 1
    },
    priorityBadge: {
      fontSize: '11px',
      fontWeight: '600',
      padding: '3px 10px',
      borderRadius: '10px',
      color: 'white',
      marginLeft: '10px'
    },
    metricsGrid: {
      display: 'grid',
      gridTemplateColumns: '1fr 1fr',
      gap: '16px',
      marginBottom: '16px'
    },
    metric: {
      display: 'flex',
      flexDirection: 'column'
    },
    metricLabel: {
      fontSize: '12px',
      color: '#86868b',
      marginBottom: '3px',
      fontWeight: '500'
    },
    metricValue: {
      fontSize: '20px',
      fontWeight: '700',
      color: '#1d1d1f'
    },
    partnersSection: {
      marginBottom: '16px'
    },
    partnersLabel: {
      fontSize: '12px',
      color: '#86868b',
      marginBottom: '6px',
      fontWeight: '500'
    },
    partnersContainer: {
      display: 'flex',
      gap: '6px',
      flexWrap: 'wrap'
    },
    partnerTag: {
      fontSize: '11px',
      padding: '4px 10px',
      borderRadius: '12px',
      backgroundColor: '#f5f5f7',
      color: '#1d1d1f',
      border: '1px solid #d2d2d7'
    },
    footer: {
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'space-between'
    },
    confidence: {
      display: 'flex',
      alignItems: 'center',
      fontSize: '12px',
      color: '#34c759'
    },
    confidenceDot: {
      width: '5px',
      height: '5px',
      borderRadius: '50%',
      backgroundColor: '#34c759',
      marginRight: '5px'
    },
    analyzeButton: {
      backgroundColor: '#ff6b35',
      color: 'white',
      border: 'none',
      borderRadius: '16px',
      padding: '8px 16px',
      fontSize: '13px',
      fontWeight: '600',
      cursor: 'pointer',
      transition: 'all 0.2s ease'
    }
  };

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <div style={styles.logo}>
          <div style={styles.logoIcon}>
            <TrendingUp size={18} />
          </div>
          Grip
        </div>
        <h1 style={styles.title}>AI-Discovered Opportunities</h1>
        <p style={styles.subtitle}>{opportunities.length} Active</p>
      </div>

      <div style={styles.content}>
        {opportunities.map((opportunity) => (
          <div key={opportunity.id} style={styles.card}>
            <div style={styles.cardHeader}>
              <h2 style={styles.cardTitle}>{opportunity.title}</h2>
              <span 
                style={{
                  ...styles.priorityBadge,
                  backgroundColor: opportunity.priorityColor
                }}
              >
                {opportunity.priority}
              </span>
            </div>

            <div style={styles.metricsGrid}>
              <div style={styles.metric}>
                <span style={styles.metricLabel}>Projected ROI</span>
                <span style={styles.metricValue}>{opportunity.projectedROI}</span>
              </div>
              <div style={styles.metric}>
                <span style={styles.metricLabel}>Timeline</span>
                <span style={styles.metricValue}>{opportunity.timeline}</span>
              </div>
            </div>

            <div style={styles.partnersSection}>
              <div style={styles.partnersLabel}>Key Partners</div>
              <div style={styles.partnersContainer}>
                {opportunity.partners.map((partner, idx) => (
                  <span key={idx} style={styles.partnerTag}>{partner}</span>
                ))}
              </div>
            </div>

            <div style={styles.footer}>
              <div style={styles.confidence}>
                <span style={styles.confidenceDot}></span>
                Confidence: {opportunity.confidence}
              </div>
              <button 
                style={styles.analyzeButton}
                onClick={() => handleAnalyze(opportunity)}
                onMouseEnter={(e) => {
                  e.target.style.backgroundColor = '#e55a2b';
                }}
                onMouseLeave={(e) => {
                  e.target.style.backgroundColor = '#ff6b35';
                }}
              >
                Analyze
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

// Modified InvestorDashboard to handle the specific CSV format
const InvestorDashboard = ({ initialData, onBack }) => {
  const [formData, setFormData] = useState({
    profileText: initialData?.profileText || '',
    transactionsFile: null,
    paymentsFile: null
  });
  const [results, setResults] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [fileLoadStatus, setFileLoadStatus] = useState('');
  const [csvPreview, setCsvPreview] = useState({ transactions: null, payments: null });

  // Function to validate and preview CSV structure
  const validateCSVStructure = (data, type) => {
    if (!data || data.length === 0) return { valid: false, message: 'File is empty' };

    const headers = Object.keys(data[0]).map(h => h.trim().toLowerCase());

    if (type === 'transactions') {
      const requiredColumns = ['transaction_id', 'date', 'revenue', 'cost'];
      const missingColumns = requiredColumns.filter(col => !headers.includes(col));

      if (missingColumns.length > 0) {
        return { 
          valid: false, 
          message: `Missing required columns: ${missingColumns.join(', ')}. Found: ${headers.join(', ')}` 
        };
      }

      // Check for valid data in first few rows
      const sampleData = data.slice(0, Math.min(3, data.length));
      const hasValidData = sampleData.some(row => 
        row.revenue && !isNaN(parseFloat(row.revenue)) && 
        row.cost && !isNaN(parseFloat(row.cost)) &&
        row.date
      );

      if (!hasValidData) {
        return { 
          valid: false, 
          message: 'No valid revenue/cost data found in first few rows' 
        };
      }

      return { 
        valid: true, 
        message: `✓ Valid transactions file (${data.length} rows)`,
        preview: sampleData
      };
    }

    if (type === 'payments') {
      const requiredColumns = ['payment_id', 'date', 'amount_paid'];
      const missingColumns = requiredColumns.filter(col => !headers.includes(col));

      if (missingColumns.length > 0) {
        return { 
          valid: false, 
          message: `Missing required columns: ${missingColumns.join(', ')}. Found: ${headers.join(', ')}` 
        };
      }

      const sampleData = data.slice(0, Math.min(3, data.length));
      const hasValidData = sampleData.some(row => 
        row.amount_paid && !isNaN(parseFloat(row.amount_paid)) &&
        row.date
      );

      if (!hasValidData) {
        return { 
          valid: false, 
          message: 'No valid payment amount data found in first few rows' 
        };
      }

      return { 
        valid: true, 
        message: `✓ Valid payments file (${data.length} rows)`,
        preview: sampleData
      };
    }

    return { valid: false, message: 'Unknown file type' };
  };

  // Auto-load CSV files when coming from opportunities
  React.useEffect(() => {
    const loadCSVFiles = async () => {
      if (initialData?.transactionsFile || initialData?.paymentsFile) {
        setFileLoadStatus('Loading CSV files...');
        try {
          let transactionsFile = null;
          let paymentsFile = null;

          // Load transactions file
          if (initialData.transactionsFile) {
            try {
              const transactionsResponse = await fetch(`src/public/${initialData.transactionsFile}`);
              if (transactionsResponse.ok) {
                const transactionsBlob = await transactionsResponse.blob();
                transactionsFile = new File([transactionsBlob], initialData.transactionsFile, { type: 'text/csv' });
              }
            } catch (err) {
              console.warn(`Could not load transactions file: ${initialData.transactionsFile}`);
            }
          }

          // Load payments file
          if (initialData.paymentsFile) {
            try {
              const paymentsResponse = await fetch(`src/public/${initialData.paymentsFile}`);
              if (paymentsResponse.ok) {
                const paymentsBlob = await paymentsResponse.blob();
                paymentsFile = new File([paymentsBlob], initialData.paymentsFile, { type: 'text/csv' });
              }
            } catch (err) {
              console.warn(`Could not load payments file: ${initialData.paymentsFile}`);
            }
          }

          setFormData(prev => ({
            ...prev,
            transactionsFile,
            paymentsFile
          }));

          if (transactionsFile || paymentsFile) {
            setFileLoadStatus(`✓ Loaded ${transactionsFile ? '1' : '0'} transactions file${transactionsFile && paymentsFile ? ' and 1 payments file' : paymentsFile ? ' and 1 payments file' : ''}`);
          } else {
            setFileLoadStatus('⚠️ Could not load CSV files from public directory');
          }
        } catch (err) {
          setFileLoadStatus('❌ Failed to load CSV files');
          console.error('Error loading CSV files:', err);
        }
      }
    };

    loadCSVFiles();
  }, [initialData]);

  const handleFileChange = async (field, file) => {
    if (!file) return;

    setFormData(prev => ({
      ...prev,
      [field]: file
    }));
    setFileLoadStatus(''); // Clear auto-load status when user manually uploads

    // Preview CSV file structure
    try {
      const text = await file.text();
      const lines = text.split('\n').slice(0, 4); // Header + 3 sample rows
      const Papa = await import('https://cdnjs.cloudflare.com/ajax/libs/PapaParse/5.3.0/papaparse.min.js');

      const parsed = Papa.parse(text, {
        header: true,
        skipEmptyLines: true,
        dynamicTyping: true
      });

      const fileType = field === 'transactionsFile' ? 'transactions' : 'payments';
      const validation = validateCSVStructure(parsed.data, fileType);

      if (validation.valid) {
        setCsvPreview(prev => ({
          ...prev,
          [fileType]: {
            headers: Object.keys(parsed.data[0] || {}),
            sampleRows: validation.preview,
            message: validation.message
          }
        }));
      } else {
        setError(`${fileType} file validation failed: ${validation.message}`);
        setCsvPreview(prev => ({
          ...prev,
          [fileType]: null
        }));
      }
    } catch (err) {
      setError(`Error reading ${field}: ${err.message}`);
    }
  };

  const handleSubmit = async () => {
    if (!formData.profileText || !formData.transactionsFile) {
      setError('Please provide company profile and transactions file');
      return;
    }

    setLoading(true);
    setError('');

    try {
      const formDataObj = new FormData();
      formDataObj.append('profile_text', formData.profileText);
      formDataObj.append('transactions_file', formData.transactionsFile);
      if (formData.paymentsFile) {
        formDataObj.append('payments_file', formData.paymentsFile);
      }

      const response = await fetch('http://localhost:8000/investor_opportunity', {
        method: 'POST',
        body: formDataObj
      });

      const data = await response.json();

      if (data.error) {
        setError(data.error + (data.detail ? ': ' + data.detail : ''));
      } else {
        setResults(data);
      }
    } catch (err) {
      setError('Failed to analyze opportunity: ' + err.message);
    } finally {
      setLoading(false);
    }
  };

  const downloadReport = async () => {
    try {
      const response = await fetch('http://localhost:8000/download_report', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(results)
      });

      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.style.display = 'none';
      a.href = url;
      a.download = 'investment_report.pdf';
      document.body.appendChild(a);
      a.click();
      window.URL.revokeObjectURL(url);
      document.body.removeChild(a);
    } catch (err) {
      setError('Failed to download report: ' + err.message);
    }
  };

  const formatCurrency = (value) => {
    if (value === null || value === undefined) return 'N/A';
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
