import React, { useState } from "react";
import axios from "axios";
import { Card, CardContent } from "@mui/material";
import { PieChart, Pie, Cell, BarChart, Bar, XAxis, YAxis, Tooltip, ResponsiveContainer } from "recharts";

function App() {
  const [profile, setProfile] = useState("");
  const [transactions, setTransactions] = useState(null);
  const [payments, setPayments] = useState(null);
  const [result, setResult] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!transactions) return alert("Please upload transactions file.");

    const formData = new FormData();
    formData.append("profile_text", profile);
    formData.append("transactions_file", transactions);
    if (payments) formData.append("payments_file", payments);

    try {
      const res = await axios.post("http://127.0.0.1:8000/investor_opportunity", formData);
      setResult(res.data);
    } catch (err) {
      console.error(err);
      alert("Error: " + err.message);
    }
  };

  const downloadJSON = () => {
    const blob = new Blob([JSON.stringify(result, null, 2)], { type: "application/json" });
    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = "investment_analysis.json";
    a.click();
  };

  // Colors for charts
  const COLORS = ["#4CAF50", "#F44336", "#2196F3"];

  // Pie chart data for profit breakdown
  const pieData = result
    ? [
        { name: "Profit", value: result.metrics.net_profit },
        { name: "Cost", value: result.metrics.total_cost }
      ]
    : [];

  return (
    <div style={{ padding: "20px", fontFamily: "Arial" }}>
      <h1>📈 Investor Opportunity Dashboard</h1>
      <form onSubmit={handleSubmit} style={{ marginBottom: "20px" }}>
        <textarea
          placeholder="Enter company profile..."
          value={profile}
          onChange={(e) => setProfile(e.target.value)}
          style={{ width: "100%", height: "80px", marginBottom: "10px" }}
        />
        <br />
        <input type="file" onChange={(e) => setTransactions(e.target.files[0])} />
        <br />
        <input type="file" onChange={(e) => setPayments(e.target.files[0])} />
        <br /><br />
        <button type="submit">Analyze</button>
      </form>

      {result && (
        <div>
          {/* KPI Cards */}
          <div style={{ display: "flex", gap: "15px", marginBottom: "20px" }}>
            <Card style={{ flex: 1, background: "#4CAF50", color: "white" }}>
              <CardContent>
                <h3>Total Revenue</h3>
                <p>${result.metrics.total_revenue?.toLocaleString()}</p>
              </CardContent>
            </Card>
            <Card style={{ flex: 1, background: "#2196F3", color: "white" }}>
              <CardContent>
                <h3>ROI</h3>
                <p>{result.metrics.roi_percent?.toFixed(2) || "N/A"}%</p>
              </CardContent>
            </Card>
            <Card style={{ flex: 1, background: "#FF9800", color: "white" }}>
              <CardContent>
                <h3>Growth</h3>
                <p>{result.metrics.growth_rate_percent?.toFixed(2) || "N/A"}%</p>
              </CardContent>
            </Card>
            <Card style={{ flex: 1, background: "#9C27B0", color: "white" }}>
              <CardContent>
                <h3>Valuation</h3>
                <p>${result.valuation_estimate?.toLocaleString()}</p>
              </CardContent>
            </Card>
            <Card style={{ flex: 1, background: "#F44336", color: "white" }}>
              <CardContent>
                <h3>Risk</h3>
                <p>{result.risk?.risk_score}</p>
              </CardContent>
            </Card>
          </div>

          {/* Charts */}
          <div style={{ display: "flex", gap: "30px", marginBottom: "20px" }}>
            <div style={{ flex: 1, height: 300 }}>
              <h3>Profit vs Cost</h3>
              <ResponsiveContainer>
                <PieChart>
                  <Pie data={pieData} cx="50%" cy="50%" outerRadius={100} dataKey="value">
                    {pieData.map((_, index) => (
                      <Cell key={index} fill={COLORS[index % COLORS.length]} />
                    ))}
                  </Pie>
                  <Tooltip />
                </PieChart>
              </ResponsiveContainer>
            </div>

            <div style={{ flex: 2, height: 300 }}>
              <h3>ROI Projections</h3>
              <ResponsiveContainer>
                <BarChart
                  data={[
                    { name: "Baseline Min", value: result.roi_projections.baseline?.roi_percent_min_investment },
                    { name: "Baseline Max", value: result.roi_projections.baseline?.roi_percent_max_investment },
                    { name: "Uplift Min", value: result.roi_projections.with_uplift?.projected_roi_percent_min_investment },
                    { name: "Uplift Max", value: result.roi_projections.with_uplift?.projected_roi_percent_max_investment }
                  ]}
                >
                  <XAxis dataKey="name" />
                  <YAxis />
                  <Tooltip />
                  <Bar dataKey="value" fill="#4CAF50" />
                </BarChart>
              </ResponsiveContainer>
            </div>
          </div>

          {/* AI Investment Advice */}
          <Card style={{ marginBottom: "20px" }}>
