import React, { useState } from "react";
import axios from "axios";

function App() {
  const [profile, setProfile] = useState("");
  const [transactions, setTransactions] = useState(null);
  const [payments, setPayments] = useState(null);
  const [result, setResult] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!transactions) return alert("Please upload transactions file.");

    const formData = new FormData();
    formData.append("profile_text", profile);
    formData.append("transactions_file", transactions);
    if (payments) formData.append("payments_file", payments);

    try {
      const res = await axios.post("http://127.0.0.1:8000/investor_opportunity", formData);
      setResult(res.data);
    } catch (err) {
      console.error(err);
      alert("Error: " + err.message);
    }
  };

  const downloadJSON = () => {
    const blob = new Blob([JSON.stringify(result, null, 2)], { type: "application/json" });
    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = "investment_analysis.json";
    a.click();
  };

  const downloadPDF = async () => {
    try {
      const res = await axios.post("http://127.0.0.1:8000/download_report", result, {
        responseType: "blob"
      });
      const url = URL.createObjectURL(new Blob([res.data]));
      const a = document.createElement("a");
      a.href = url;
      a.download = "investment_report.pdf";
      a.click();
    } catch (err) {
      console.error("Error downloading PDF:", err);
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>Investor Opportunity Engine</h1>
      <form onSubmit={handleSubmit}>
        <textarea
          placeholder="Enter company profile..."
          value={profile}
          onChange={(e) => setProfile(e.target.value)}
          style={{ width: "100%", height: "80px", marginBottom: "10px" }}
        />
        <br />
        <input type="file" onChange={(e) => setTransactions(e.target.files[0])} />
        <br />
        <input type="file" onChange={(e) => setPayments(e.target.files[0])} />
        <br /><br />
        <button type="submit">Analyze</button>
      </form>

      {result && (
        <div style={{ marginTop: "20px" }}>
          <h2>ðŸ“Š Analysis Results</h2>
          <pre>{JSON.stringify(result.llm_investment_advice, null, 2)}</pre>

          <button onClick={downloadJSON} style={{ marginRight: "10px" }}>
            Download JSON
          </button>
          <button onClick={downloadPDF}>
            Download PDF
          </button>
        </div>
      )}
    </div>
  );
}

export default App;
