import React, { useState } from "react";
import axios from "axios";

function App() {
  const [profileText, setProfileText] = useState("");
  const [transactionsFile, setTransactionsFile] = useState(null);
  const [paymentsFile, setPaymentsFile] = useState(null);
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError("");
    setResult(null);

    try {
      const formData = new FormData();
      formData.append("profile_text", profileText);
      formData.append("transactions_file", transactionsFile);
      if (paymentsFile) {
        formData.append("payments_file", paymentsFile);
      }

      const res = await axios.post("http://127.0.0.1:8000/investor_opportunity", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });

      setResult(res.data);
    } catch (err) {
      setError(err.response?.data || err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>Investor Opportunity Engine</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Company Profile</label>
          <textarea
            value={profileText}
            onChange={(e) => setProfileText(e.target.value)}
            rows={5}
            style={{ width: "100%", marginBottom: "10px" }}
            placeholder="Enter company background info here..."
          />
        </div>

        <div>
          <label>Transactions File (.csv or .xlsx)</label>
          <input
            type="file"
            accept=".csv, .xlsx"
            onChange={(e) => setTransactionsFile(e.target.files[0])}
            required
          />
        </div>

        <div>
          <label>Payments File (.csv or .xlsx) â€” optional</label>
          <input
            type="file"
            accept=".csv, .xlsx"
            onChange={(e) => setPaymentsFile(e.target.files[0])}
          />
        </div>

        <button type="submit" disabled={loading} style={{ marginTop: "10px" }}>
          {loading ? "Analyzing..." : "Submit"}
        </button>
      </form>

      {error && (
        <div style={{ marginTop: "20px", color: "red" }}>
          <strong>Error:</strong> {JSON.stringify(error)}
        </div>
      )}

      {result && (
        <div style={{ marginTop: "20px" }}>
          <h2>Analysis Result</h2>
          <pre style={{ background: "#f4f4f4", padding: "10px" }}>
            {JSON.stringify(result, null, 2)}
          </pre>
        </div>
      )}
    </div>
  );
}

export default App;
