import React, { useState } from 'react';
import { TrendingUp, Upload, FileText, DollarSign, AlertTriangle, Download, Calculator, BarChart3, Target, Clock, Shield, ArrowLeft } from 'lucide-react';

// OpportunitiesList Component
const OpportunitiesList = ({ onAnalyze }) => {
  const opportunities = [
    {
      id: 1,
      title: "Instant health loans",
      priority: "High Priority",
      priorityColor: "#f97316",
      projectedROI: "340%",
      timeline: "6 months",
      partners: ["Ayala Healthcare", "Globe Telecom"],
      confidence: "93%",
      profileText: "Healthcare fintech startup providing instant medical loans to underserved populations. Uses AI for credit scoring and partners with major healthcare providers. Strong growth trajectory with 40% month-over-month user acquisition.",
      transactionsFile: "health-loans-transactions.csv",
      paymentsFile: "health-loans-payments.csv"
    },
    {
      id: 2,
      title: "Climate-based Insurance",
      priority: "Medium Priority",
      priorityColor: "#eab308",
      projectedROI: "185%",
      timeline: "8 months",
      partners: ["Weather Analytics", "AgriTech Corp"],
      confidence: "78%",
      profileText: "Insurtech company offering climate-risk insurance for farmers and small businesses. Uses satellite data and weather analytics for precise risk assessment. Growing demand due to climate change impacts.",
      transactionsFile: "climate-insurance-transactions.csv",
      paymentsFile: "climate-insurance-payments.csv"
    },
    {
      id: 3,
      title: "Rural MSMEs Cash Flow",
      priority: "High Priority",
      priorityColor: "#f97316",
      projectedROI: "185%",
      timeline: "6 months",
      partners: ["AgriTech Corp", "Rural Bank Network"],
      confidence: "78%",
      profileText: "Fintech platform providing working capital loans to rural micro, small and medium enterprises. Focus on agricultural supply chain financing with digital-first approach and alternative credit scoring.",
      transactionsFile: "rural-msmes-transactions.csv",
      paymentsFile: "rural-msmes-payments.csv"
    }
  ];

  const handleAnalyze = (opportunity) => {
    if (onAnalyze) {
      onAnalyze({
        profileText: opportunity.profileText,
        transactionsFile: opportunity.transactionsFile,
        paymentsFile: opportunity.paymentsFile,
        opportunityTitle: opportunity.title
      });
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 font-sans">
      <div className="bg-gradient-to-br from-indigo-500 to-purple-600 text-white py-10 px-5 text-center">
        <div className="flex items-center justify-center mb-4 text-3xl font-bold">
          <div className="w-9 h-9 mr-3 bg-gradient-to-br from-orange-500 to-yellow-500 rounded-xl flex items-center justify-center">
            <TrendingUp size={18} />
          </div>
          Grip
        </div>
        <h1 className="text-2xl font-bold mb-2">AI-Discovered Opportunities</h1>
        <p className="text-sm opacity-90">{opportunities.length} Active</p>
      </div>

      <div className="max-w-sm mx-auto p-4">
        {opportunities.map((opportunity) => (
          <div key={opportunity.id} className="bg-white rounded-2xl p-5 mb-3 shadow-lg border border-gray-100">
            <div className="flex items-start justify-between mb-4">
              <h2 className="text-lg font-semibold text-gray-900 flex-1 mr-3">{opportunity.title}</h2>
              <span 
                className="text-xs font-semibold px-3 py-1 rounded-xl text-white ml-2"
                style={{ backgroundColor: opportunity.priorityColor }}
              >
                {opportunity.priority}
              </span>
            </div>

            <div className="grid grid-cols-2 gap-4 mb-4">
              <div className="flex flex-col">
                <span className="text-xs text-gray-500 mb-1 font-medium">Projected ROI</span>
                <span className="text-xl font-bold text-gray-900">{opportunity.projectedROI}</span>
              </div>
              <div className="flex flex-col">
                <span className="text-xs text-gray-500 mb-1 font-medium">Timeline</span>
                <span className="text-xl font-bold text-gray-900">{opportunity.timeline}</span>
              </div>
            </div>

            <div className="mb-4">
              <div className="text-xs text-gray-500 mb-2 font-medium">Key Partners</div>
              <div className="flex gap-1.5 flex-wrap">
                {opportunity.partners.map((partner, idx) => (
                  <span key={idx} className="text-xs px-3 py-1 rounded-xl bg-gray-100 text-gray-900 border border-gray-300">
                    {partner}
                  </span>
                ))}
              </div>
            </div>

            <div className="flex items-center justify-between">
              <div className="flex items-center text-xs text-green-600">
                <span className="w-1.5 h-1.5 rounded-full bg-green-600 mr-1.5"></span>
                Confidence: {opportunity.confidence}
              </div>
              <button 
                className="bg-orange-500 hover:bg-orange-600 text-white border-none rounded-2xl px-4 py-2 text-xs font-semibold cursor-pointer transition-all duration-200"
                onClick={() => handleAnalyze(opportunity)}
              >
                Analyze
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

// InvestorDashboard Component
const InvestorDashboard = ({ opportunityData, onBack }) => {
  const [results, setResults] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async () => {
    if (!opportunityData) {
      setError('No opportunity data provided');
      return;
    }

    setLoading(true);
    setError('');

    try {
      // Simulate API call - replace with actual file fetching and API call
      const simulatedResults = {
        metrics: {
          total_revenue: 2500000,
          net_profit: 450000,
          growth_rate_percent: 28.5,
          months_of_data: 12
        },
        valuation_estimate: 12500000,
        recommended_investment_range: {
          min: 1000000,
          max: 3000000
        },
        annualized_revenue_estimate: 3200000,
        risk: {
          risk_score: 'Medium',
          drivers: [
            'Market competition increasing',
            'Regulatory changes possible',
            'Customer acquisition cost rising'
          ]
        },
        roi_projections: {
          baseline: {
            roi_percent_min_investment: 25.2,
            roi_percent_max_investment: 18.7
          },
          with_uplift: {
            projected_roi_percent_min_investment: 34.8,
            projected_roi_percent_max_investment: 26.3
          }
        },
        llm_investment_advice: {
          investment_summary: `${opportunityData.opportunityTitle} shows strong fundamentals with consistent revenue growth and expanding market opportunity. The company demonstrates solid execution capabilities with established partnerships.`,
          recommended_investment_amount: 2000000,
          expected_payback_months: 36,
          confidence: 'High confidence based on financial performance and market positioning',
          high_level_strategy: [
            'Focus on scaling customer acquisition',
            'Strengthen operational infrastructure',
            'Expand into adjacent market segments',
            'Build strategic partnerships'
          ],
          main_risks: [
            'Market saturation risk in core segments',
            'Regulatory compliance requirements',
            'Competition from established players'
          ]
        }
      };

      // Simulate API delay
      await new Promise(resolve => setTimeout(resolve, 2000));
      setResults(simulatedResults);

    } catch (err) {
      setError('Failed to analyze opportunity: ' + err.message);
    } finally {
      setLoading(false);
    }
  };

  const downloadReport = async () => {
    try {
      const reportContent = `Investment Report: ${opportunityData.opportunityTitle}`;
      const blob = new Blob([reportContent], { type: 'text/plain' });
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.style.display = 'none';
      a.href = url;
      a.download = `${opportunityData.opportunityTitle.replace(/\s+/g, '_')}_investment_report.txt`;
      document.body.appendChild(a);
      a.click();
      window.URL.revokeObjectURL(url);
      document.body.removeChild(a);
    } catch (err) {
      setError('Failed to download report: ' + err.message);
    }
  };

  const formatCurrency = (value) => {
    if (value === null || value === undefined) return 'N/A';
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0
    }).format(value);
  };

  const formatPercent = (value) => {
    if (value === null || value === undefined) return 'N/A';
    return `${value.toFixed(1)}%`;
  };

  const getRiskColor = (risk) => {
    switch (risk?.toLowerCase()) {
      case 'low': return 'text-green-700 bg-green-50 border-green-200';
      case 'medium': return 'text-yellow-700 bg-yellow-50 border-yellow-200';
      case 'high': return 'text-red-700 bg-red-50 border-red-200';
      default: return 'text-gray-700 bg-gray-50 border-gray-200';
    }
  };

  React.useEffect(() => {
    const style = document.createElement('style');
    style.textContent = `
      @keyframes spin {
        0% { transform: rotate(0deg); }
        100% { transform: rotate(360deg); }
      }
    `;
    document.head.appendChild(style);
    return () => document.head.removeChild(style);
  }, []);

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 to-blue-50 font-sans">
      <div className="max-w-6xl mx-auto p-8">
        {/* Back Button */}
        <button 
          onClick={onBack}
          className="flex items-center gap-2 bg-white border border-gray-300 rounded-lg px-4 py-3 mb-6 hover:bg-gray-50 transition-colors text-gray-700 text-sm font-medium"
        >
          <ArrowLeft size={16} />
          Back to Opportunities
        </button>

        {/* Header */}
        <div className="text-center mb-12">
          <h1 className="text-4xl font-bold text-gray-900 mb-4 flex items-center justify-center gap-3">
            <Calculator size={40} className="text-blue-600" />
            Investment Analysis
          </h1>
          <p className="text-xl text-gray-600 max-w-2xl mx-auto">
            AI-powered analysis for {opportunityData?.opportunityTitle}
          </p>
        </div>

        {/* Opportunity Overview */}
        <div className="bg-white rounded-2xl shadow-xl border border-gray-200 mb-8 overflow-hidden">
          <div className="bg-gradient-to-r from-blue-600 to-purple-600 text-white p-6">
            <h2 className="text-2xl font-bold flex items-center gap-3">
              <FileText size={24} />
              Opportunity Overview
            </h2>
          </div>

          <div className="p-8">
            <div className="bg-slate-50 rounded-xl p-6 mb-6 border border-slate-200">
              <h3 className="text-xl font-bold text-gray-900 mb-3">{opportunityData?.opportunityTitle}</h3>
              <p className="text-gray-700 leading-relaxed mb-4">{opportunityData?.profileText}</p>

              <div className="flex gap-4">
                <div className="flex items-center gap-2 text-sm text-gray-600 bg-white px-3 py-2 rounded-lg border border-gray-200">
                  <FileText size={16} />
                  {opportunityData?.transactionsFile}
                </div>
                <div className="flex items-center gap-2 text-sm text-gray-600 bg-white px-3 py-2 rounded-lg border border-gray-200">
                  <FileText size={16} />
                  {opportunityData?.paymentsFile}
                </div>
              </div>
            </div>

            {error && (
              <div className="mt-6 p-4 bg-red-50 border border-red-200 rounded-lg">
                <div className="flex items-center gap-2 text-red-700">
                  <AlertTriangle size={20} />
                  <span>{error}</span>
                </div>
              </div>
            )}

            <button
              onClick={handleSubmit}
              disabled={loading}
              className={`w-full bg-gradient-to-r from-blue-600 to-purple-600 text-white py-4 px-8 rounded-lg font-semibold text-lg border-none cursor-pointer transition-all flex items-center justify-center gap-3 mt-8 ${
                loading ? 'opacity-50 cursor-not-allowed' : 'hover:shadow-lg hover:-translate-y-0.5'
              }`}
            >
              {loading ? (
                <>
                  <div 
                    className="w-6 h-6 border-2 border-transparent border-t-white rounded-full"
                    style={{animation: 'spin 1s linear infinite'}}
                  ></div>
                  Analyzing Investment Opportunity...
                </>
              ) : (
                <>
                  <BarChart3 size={24} />
                  Analyze Investment Opportunity
                </>
              )}
            </button>
          </div>
        </div>

        {/* Results */}
        {results && (
          <div className="grid gap-8">
            {/* Key Metrics Cards */}
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8">
              <div className="bg-white rounded-xl shadow-lg border border-gray-200 p-6">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-sm font-medium text-gray-500">Total Revenue</p>
                    <p className="text-3xl font-bold text-gray-900">{formatCurrency(results.metrics?.total_revenue)}</p>
                  </div>
                  <DollarSign size={32} className="text-green-600" />
                </div>
              </div>

              <div className="bg-white rounded-xl shadow-lg border border-gray-200 p-6">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-sm font-medium text-gray-500">Net Profit</p>
                    <p className="text-3xl font-bold text-gray-900">{formatCurrency(results.metrics?.net_profit)}</p>
                  </div>
                  <Target size={32} className="text-blue-600" />
                </div>
              </div>

              <div className="bg-white rounded-xl shadow-lg border border-gray-200 p-6">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-sm font-medium text-gray-500">Growth Rate</p>
                    <p className="text-3xl font-bold text-gray-900">{formatPercent(results.metrics?.growth_rate_percent)}</p>
                  </div>
                  <TrendingUp size={32} className="text-purple-600" />
                </div>
              </div>

              <div className="bg-white rounded-xl shadow-lg border border-gray-200 p-6">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-sm font-medium text-gray-500">Data Period</p>
                    <p className="text-3xl font-bold text-gray-900">{results.metrics?.months_of_data} mo</p>
                  </div>
                  <Clock size={32} className="text-yellow-600" />
                </div>
              </div>
            </div>

            {/* Valuation and Risk */}
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
              <div className="bg-white rounded-xl shadow-lg border border-gray-200">
                <div className="p-8">
                  <h3 className="text-xl font-bold text-gray-900 mb-6 flex items-center gap-3">
                    <Calculator size={24} className="text-blue-600" />
                    Valuation & Investment Range
                  </h3>
                  <div className="space-y-3">
                    <div className="flex justify-between items-center py-3 border-b border-gray-100">
                      <span className="text-gray-600">Estimated Valuation</span>
                      <span className="font-semibold text-lg">{formatCurrency(results.valuation_estimate)}</span>
                    </div>
                    <div className="flex justify-between items-center py-3 border-b border-gray-100">
                      <span className="text-gray-600">Investment Range (Min)</span>
                      <span className="font-semibold">{formatCurrency(results.recommended_investment_range?.min)}</span>
                    </div>
                    <div className="flex justify-between items-center py-3 border-b border-gray-100">
                      <span className="text-gray-600">Investment Range (Max)</span>
                      <span className="font-semibold">{formatCurrency(results.recommended_investment_range?.max)}</span>
                    </div>
                    <div className="flex justify-between items-center py-3">
                      <span className="text-gray-600">Annualized Revenue</span>
                      <span className="font-semibold">{formatCurrency(results.annualized_revenue_estimate)}</span>
                    </div>
                  </div>
                </div>
              </div>

              <div className="bg-white rounded-xl shadow-lg border border-gray-200">
                <div className="p-8">
                  <h3 className="text-xl font-bold text-gray-900 mb-6 flex items-center gap-3">
                    <Shield size={24} className="text-red-600" />
                    Risk Assessment
                  </h3>
                  <div>
                    <div className={`inline-flex px-4 py-2 rounded-full font-semibold text-sm border-2 ${getRiskColor(results.risk?.risk_score)}`}>
                      {results.risk?.risk_score || 'Unknown'} Risk
                    </div>
                    <div className="mt-4">
                      <p className="text-sm font-medium text-gray-500 mb-2">Risk Factors:</p>
                      <div className="space-y-2">
                        {results.risk?.drivers?.map((driver, idx) => (
                          <div key={idx} className="flex items-start text-sm">
                            <span className="w-2 h-2 bg-gray-400 rounded-full mt-2 mr-3 flex-shrink-0"></span>
                            {driver}
                          </div>
                        ))}
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>

            {/* ROI Projections */}
            <div className="bg-white rounded-xl shadow-lg border border-gray-200">
              <div className="p-8">
                <h3 className="text-xl font-bold text-gray-900 mb-6 flex items-center gap-3">
                  <TrendingUp size={24} className="text-green-600" />
                  ROI Projections
                </h3>
                <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
                  <div>
                    <h4 className="font-semibold text-gray-700 mb-4">Baseline ROI</h4>
                    <div className="space-y-3">
                      <div className="flex justify-between mb-3">
                        <span className="text-gray-600">Min Investment ROI</span>
                        <span className="font-semibold">{formatPercent(results.roi_projections?.baseline?.roi_percent_min_investment)}</span>
                      </div>
                      <div className="flex justify-between">
                        <span className="text-gray-600">Max Investment ROI</span>
                        <span className="font-semibold">{formatPercent(results.roi_projections?.baseline?.roi_percent_max_investment)}</span>
                      </div>
                    </div>
                  </div>
                  <div>
                    <h4 className="font-semibold text-gray-700 mb-4">With Growth Uplift</h4>
                    <div className="space-y-3">
                      <div className="flex justify-between mb-3">
                        <span className="text-gray-600">Projected Min ROI</span>
                        <span className="font-semibold text-green-600">{formatPercent(results.roi_projections?.with_uplift?.projected_roi_percent_min_investment)}</span>
                      </div>
                      <div className="flex justify-between">
                        <span className="text-gray-600">Projected Max ROI</span>
                        <span className="font-semibold text-green-600">{formatPercent(results.roi_projections?.with_uplift?.projected_roi_percent_max_investment)}</span>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>

            {/* AI Investment Advice */}
            {results.llm_investment_advice && (
              <div className="bg-gradient-to-br from-blue-50 to-indigo-100 rounded-xl border border-blue-200 p-8">
                <h3 className="text-xl font-bold text-gray-900 mb-6 flex items-center gap-3">
                  <Target size={24} className="text-blue-600" />
                  AI Investment Recommendation
                </h3>

                <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
                  <div className="space-y-6">
                    <div>
                      <h4 className="font-semibold text-gray-700 mb-2">Investment Summary</h4>
                      <p className="text-gray-900 bg-white p-4 rounded-lg border border-gray-200">{results.llm_investment_advice.investment_summary}</p>
                    </div>

                    <div>
                      <h4 className="font-semibold text-gray-700 mb-2">Recommended Amount</h4>
                      <p className="text-3xl font-bold text-blue-600">{formatCurrency(results.llm_investment_advice.recommended_investment_amount)}</p>
                    </div>

                    <div>
                      <h4 className="font-semibold text-gray-700 mb-2">Expected Payback</h4>
                      <p className="text-lg font-semibold text-gray-900">{results.llm_investment_advice.expected_payback_months} months</p>
                    </div>

                    <div>
                      <h4 className="font-semibold text-gray-700 mb-2">Confidence Level</h4>
                      <p className="text-gray-900 bg-white p-3 rounded-lg border border-gray-200">{results.llm_investment_advice.confidence}</p>
                    </div>
                  </div>

                  <div className="space-y-6">
                    <div>
                      <h4 className="font-semibold text-gray-700 mb-2">Investment Strategy</h4>
                      <div className="space-y-2">
                        {results.llm_investment_advice.high_level_strategy?.map((item, idx) => (
                          <div key={idx} className="flex items-start bg-white p-3 rounded-lg border border-gray-200">
                            <span className="w-2 h-2 bg-blue-600 rounded-full mt-2 mr-3 flex-shrink-0"></span>
                            {item}
                          </div>
                        ))}
                      </div>
                    </div>

                    <div>
                      <h4 className="font-semibold text-gray-700 mb-2">Main Risks</h4>
                      <div className="space-y-2">
                        {results.llm_investment_advice.main_risks?.map((risk, idx) => (
                          <div key={idx} className="flex items-start bg-white p-3 rounded-lg border border-gray-200">
                            <AlertTriangle size={16} className="text-red-600 mt-1 mr-3 flex-shrink-0" />
                            {risk}
                          </div>
                        ))}
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            )}

            {/* Download Report Button */}
            <div className="text-center">
              <button
                onClick={downloadReport}
                className="bg-gradient-to-r from-green-600 to-blue-600 text-white py-4 px-8 rounded-lg font-semibold text-lg border-none cursor-pointer transition-all inline-flex items-center gap-3 hover:scale-105 hover:shadow-xl"
              >
                <Download size={24} />
                Download Investment Report (PDF)
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

// Main App Component
const App = () => {
  const [currentView, setCurrentView] = useState('opportunities');
  const [selectedOpportunity, setSelectedOpportunity] = useState(null);

  const handleAnalyzeOpportunity = (opportunityData) => {
    setSelectedOpportunity(opportunityData);
    setCurrentView('dashboard');
  };

  const handleBackToOpportunities = () => {
    setCurrentView('opportunities');
    setSelectedOpportunity(null);
  };

  return (
    <div>
      {currentView === 'opportunities' ? (
        <OpportunitiesList onAnalyze={handleAnalyzeOpportunity} />
      ) : (
        <InvestorDashboard 
          opportunityData={selectedOpportunity} 
          onBack={handleBackToOpportunities}
        />
      )}
    </div>
  );
};

export default App;
